import { useEffect } from 'react';
import './App.css';
import TitleType from './components/TitleType';
import CardScroll from './components/CardScroll';
import MechButton from './components/MechButton';
import Typewriter from 'typewriter-effect';
import Aos from 'aos';
import 'aos/dist/aos.css';
import Portrait from './assets/myself.jpeg';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import GithubIcon from './assets/github.svg';
import LinkedinIcon from './assets/linkedin.svg';

function App() {

  useEffect(() => {
    Aos.init({ duration: 2000 });
  }, []);

  return (
    <div className='App'>

      <div id='home' className='header-title' data-aos='fade-up'>
        <div id='home-center'>
          <Typewriter
            options={{
                delay: 150,
                loop: true
            }}
            onInit={(typewriter) => {
                typewriter.typeString("hi, i'm salwa")
                .pauseFor(2000)
                .deleteAll()
                .typeString("say hello!")
                .pauseFor(2000)
                .deleteAll()
                .start();
            }}
          />
          <p className='header-textArea'>
            Here's a little bit about me.
          </p>
        </div>

      </div>

      <div id='about-me' data-aos='fade-up'>
        <Row>
          <Col sm={4}>
            <img style={{maxWidth: "100%"}} src={Portrait} alt='A nice picture of me!' />
          </Col>
          <Col sm={8}>
            <TitleType title={"about me"}/>
            <p className='textArea'>
              My name is Salwa Jeries, I'm a sophomore studying computer science at The University of Alabama in Huntsville. I have a background in cyber security and love to make cool websites. I want to explore software engineering and web development for a career so that I can make fun projects - like this site - for others to enjoy as well! In my free time, I like to play piano, cuddle with my hot dog (Lily), and checkout all the new local coffee shops!
            </p>
          </Col>
        </Row>
      </div>

      <div id='projects' data-aos='fade-up'>
        <TitleType title={"projects"}/>
        <CardScroll/>
      </div>

      <div id='links' data-aos='fade-up'>
        <TitleType title={"links"}/>
        <MechButton src={GithubIcon} url={"https://github.com/salsajeries"} alt={"My Github"} />
        <MechButton src={LinkedinIcon} url={"https://www.linkedin.com/in/salwa-jeries-17a146226/"} alt={"My Linkedin"} />
      </div>

    </div>
  );
}

export default App;
